import React, { useState } from 'react';
import RegisterModal from '../Modals/RegisterModal';
import LoginModal from '../Modals/LoginModal';
import BalanceModal from './Modals/BalanceModal'; // Yeni eklenen modal
import { registerUser, getUserById } from '../Functions/userFunctions';

const Header = () => {
  const [isRegisterModalOpen, setIsRegisterModalOpen] = useState(false);
  const [isLoginModalOpen, setIsLoginModalOpen] = useState(false);
  const [isBalanceModalOpen, setIsBalanceModalOpen] = useState(false); // Yeni eklenen modal
  const [user, setUser] = useState(null);

  const openRegisterModal = () => {
    setIsRegisterModalOpen(true);
  };

  const closeRegisterModal = () => {
    setIsRegisterModalOpen(false);
  };

  const openLoginModal = () => {
    setIsLoginModalOpen(true);
  };

  const closeLoginModal = () => {
    setIsLoginModalOpen(false);
  };

  const openBalanceModal = () => {
    setIsBalanceModalOpen(true);
  };

  const closeBalanceModal = () => {
    setIsBalanceModalOpen(false);
  };

  const handleLogin = (userData) => {
    // Giriş işlemi burada yapılabilir
    console.log('Logging in user:', userData);

    // Local storage'den bakiye bilgisini çek
    const storedUser = JSON.parse(localStorage.getItem('loggedInUser'));

    // Kullanıcı bilgilerini set et
    setUser({
      name: userData.username,
      balance: storedUser ? storedUser.balance : 0, // Eğer bakiye bilgisi varsa onu kullan, yoksa 0 kullan
    });

    // Giriş işlemi tamamlandıktan sonra modal'ı kapat
    closeLoginModal();
  };

  const handleLogout = () => {
    // Çıkış işlemi burada yapılabilir
    console.log('Logging out user');

    // Kullanıcı bilgilerini sıfırla
    setUser(null);
  };

  const handleRegister = (registeredUser) => {
    // Kayıt işlemi tamamlandıktan sonra modal'ı kapat
    closeRegisterModal();

    // Kayıt olan kullanıcıyı ana bileşene iletebilirsin
    setUser({
      name: registeredUser.username,
      balance: registeredUser.balance,
    });

    // İsteğe bağlı olarak başka işlemler yapabilirsin
    console.log('Kayıt işlemi tamamlandı. Kayıt olan kullanıcı:', registeredUser);
  };

  const handleBalanceUpdate = (newBalance) => {
    // Bakiye güncelleme işlemi burada yapılabilir
    console.log('Updating user balance:', newBalance);

    // Kullanıcının bakiyesini güncelle
    setUser((prevUser) => ({ ...prevUser, balance: newBalance }));

    // Bakiye güncelleme işlemi tamamlandıktan sonra modal'ı kapat
    closeBalanceModal();
  };

  return (
    <header className="bg-blue-500 p-4 flex justify-between items-center">
      <div>
        <h1 className="text-white text-2xl font-bold">FutGuess</h1>
      </div>
      <div className="text-white flex items-center space-x-4">
        {user ? (
          <>
            <p>{user.name}</p>
            <p>Bakiye: ${user.balance}</p>
            <button onClick={openBalanceModal}>Bakiye Ekle</button>
            <button onClick={handleLogout}>Çıkış Yap</button>
          </>
        ) : (
          <>
            <button onClick={openRegisterModal}>Kayıt Ol</button>
            <button onClick={openLoginModal}>Giriş Yap</button>
          </>
        )}
      </div>

      {isRegisterModalOpen && <RegisterModal onClose={closeRegisterModal} onRegister={handleRegister} />}
      {isLoginModalOpen && <LoginModal onClose={closeLoginModal} onLogin={handleLogin} />}
      {isBalanceModalOpen && (
        <BalanceModal onClose={closeBalanceModal} onBalanceUpdate={handleBalanceUpdate} user={user} />
      )}
    </header>
  );
};

export default Header;
