// BalanceModal.js

import React, { useState } from 'react';
import { updateUserBalance } from '../Functions/userFunctions';

const BalanceModal = ({ onClose, onBalanceUpdate, user }) => {
  const [newBalance, setNewBalance] = useState('');

  const handleUpdateBalance = () => {
    const updatedBalance = parseFloat(newBalance);

    if (!isNaN(updatedBalance)) {
      // Eğer geçerli bir sayı ise güncelle
      onBalanceUpdate(updatedBalance);

      // Yeni eklenen kod: Kullanıcının bakiyesini güncelle ve kaydet
      updateUserBalance(user.id, updatedBalance);

      onClose();
    } else {
      // Hatalı giriş durumunda kullanıcıyı bilgilendir
      alert('Lütfen geçerli bir sayı girin.');
    }
  };

  return (
    <div className="fixed inset-0 overflow-y-auto flex items-center justify-center z-50">
      <div className="relative p-8 bg-white rounded-lg shadow-md max-w-md mx-auto">
        <h2 className="text-2xl font-bold mb-4">Bakiye Ekle</h2>
        <div className="mb-4">
          <label className="block text-sm font-medium text-gray-700">Bakiye ekleyin:</label>
          <input
            type="text"
            value={newBalance}
            onChange={(e) => setNewBalance(e.target.value)}
            className="mt-1 p-2 block w-full border rounded-md"
          />
        </div>
        <button
          className="bg-green-500 text-white rounded-md px-4 py-2 mt-4"
          onClick={handleUpdateBalance}
        >
          Bakiye Ekle
        </button>
        <button
          className="bg-gray-700 text-white px-4 py-2 rounded-md mt-4 ml-2"
          onClick={onClose}
        >
          İptal
        </button>
      </div>
    </div>
  );
};

export default BalanceModal;
