import React, { useState } from 'react';
import usersData from './Data/Users.json';

const Register = () => {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleRegister = () => {
    // Kullanıcıyı veritabanına eklemek ve bakiye atamak için gerekli işlemleri gerçekleştirin.
    const newUser = {
      id: usersData.users.length + 1,
      username,
      email,
      password,
      balance: 0,
      isAdmin: false // Yeni kullanıcılar admin değil
    };

    // Veritabanına yeni kullanıcıyı ekleyin
    usersData.users.push(newUser);

    // Kayıt işlemi tamamlandıktan sonra formu sıfırlayın
    setUsername('');
    setEmail('');
    setPassword('');

    console.log('Kayıt işlemi başarılı!', newUser);
  };

  return (
    <div>
      <h2>Kayıt Ol</h2>
      <div>
        <label>Kullanıcı Adı:</label>
        <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} />
      </div>
      <div>
        <label>E-posta:</label>
        <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
      </div>
      <div>
        <label>Şifre:</label>
        <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
      </div>
      <button onClick={handleRegister}>Kayıt Ol</button>
    </div>
  );
};

export default Register;
