import './App.css';

import Header from './Components/Header'
import MatchMenu from './Components/MatchMenu';
function App() {

  const [selectedDay, setSelectedDay] = useState('Matchday 1');

  const handleDaySelect = (day) => {
    setSelectedDay(day);
  };

  return (
    <>
      <div className="container mx-auto p-4">
      <header className="mb-8">
        <Header />
        <h1 className="text-2xl font-bold">Dünya Kupası 2022</h1>
      </header>
      <div className="mb-4">
        <p className="text-lg font-bold">Gün Seç:</p>
        <div className="flex space-x-4">
          {[...new Set(matchData.map((match) => match.round))].map((day) => (
            <button
              key={day}
              className={`px-4 py-2 ${
                selectedDay === day ? 'bg-blue-500 text-white' : 'bg-gray-300'
              } rounded-md`}
              onClick={() => handleDaySelect(day)}
            >
              {day}
            </button>
          ))}
        </div>
      </div>
      <MatchList matches={matchData.filter((match) => match.round === selectedDay)} />
      {/* Diğer bileşenleri ekleyebilirsiniz */}
    </div>
    </>
  );
}

export default App;
