// MatchesPage.js
import React, { useState } from 'react';
import MatchMenu from './MatchMenu';
import MatchesByMatchday from './MatchesByMatchday';
import matchData from '../Data/MatchData';

const MatchesPage = () => {
  const [selectedMatchday, setSelectedMatchday] = useState(1);

  const matchdays = Array.from({ length: 13 }, (_, index) => index + 1); // Assuming 13 matchdays

  const filteredMatches = matchData.filter((match) => match.round.includes(`Matchday ${selectedMatchday}`));

  return (
    <div className="container mx-auto">
      <MatchMenu matchdays={matchdays} onSelectMatchday={setSelectedMatchday} />
      <MatchesByMatchday matches={filteredMatches} />
    </div>
  );
};

export default MatchesPage;
